export const authGuard: CanActivateFn = () => {
  return inject(AuthService).isLoggedIn() || inject(Router).createUrlTree(['/login']);
};

private cache = new Map<string, any>();
get(key: string, fetcher: () => Observable<any>) {
  if (this.cache.has(key)) return of(this.cache.get(key));
  return fetcher().pipe(tap(data => this.cache.set(key, data)));
}

