div {
  transition: width 2s ease-in-out;
}

@keyframes example {
  0% {opacity: 0;}
  100% {opacity: 1;}
}
div {
  animation: example 3s linear;
}

button {
  transition: background-color 0.5s ease;
}
button:hover {
  background-color: blue;
}

@keyframes move {
  0% {transform: translateX(0);}
  100% {transform: translateX(100px);}
}
div {
  animation: move 2s ease-out;
}

div {
  transition: opacity 1s ease-in-out;
}
div:hover {
  opacity: 0;
}

div {
  background-color: red;
  transition: background-color 0.5s;
}
div:hover {
  background-color: green;
}

@keyframes pulse {
  0% {transform: scale(1);}
  50% {transform: scale(1.2);}
  100% {transform: scale(1);}
}
div {
  animation: pulse 1s infinite;
}

div {
  transition: width 2s ease-in-out;
  width: 100px;
}
div:hover {
  width: 200px;
}

@keyframes rotate {
  0% {transform: rotate(0deg);}
  100% {transform: rotate(360deg);}
}
div {
  animation: rotate 4s infinite linear;
}

p {
  transition: opacity 1.5s ease;
}
p:hover {
  opacity: 0.5;
}

div {
  transition: all 1s ease;
}
div:hover {
  width: 150px;
  height: 150px;
  background-color: yellow;
}

@keyframes fadeIn {
  0% {opacity: 0;}
  100% {opacity: 1;}
}
div {
  animation: fadeIn 2s 1s ease;
}

div {
  transition: transform 0.5s ease-out;
}
div:hover {
  transform: scale(1.5);
}

div {
  background-color: orange;
  transition: background-color 1s ease;
}
div:hover {
  background-color: purple;
}

@keyframes slideIn {
  from {transform: translateX(-100%);}
  to {transform: translateX(0);}
}
div {
  animation: slideIn 2s ease-in-out;
}

div {
  height: 100px;
  transition: height 1s ease-in;
}
div:hover {
  height: 200px;
}

div {
  transition: margin-left 1s ease;
}
div:hover {
  margin-left: 50px;
}

div {
  transition: background-color 1s ease-in-out, transform 2s ease;
}
div:hover {
  background-color: yellow;
  transform: rotate(45deg);
}

div {
  transition: width 2s linear;
}
div:hover {
  width: 300px;
}

@keyframes colorChange {
  0% {background-color: red;}
  100% {background-color: blue;}
}
div {
  animation: colorChange 3s infinite;
}

div {
  transition: border-color 1s ease;
  border: 2px solid black;
}
div:hover {
  border-color: red;
}

@keyframes grow {
  0% {height: 100px;}
  100% {height: 300px;}
}
div {
  animation: grow 4s ease-in-out;
}

button {
  transition: font-size 0.5s ease-out;
}
button:active {
  font-size: 20px;
}

@keyframes bounce {
  0% {transform: translateY(0);}
  50% {transform: translateY(-50px);}
  100% {transform: translateY(0);}
}
div {
  animation: bounce 2s infinite;
}

@keyframes rotate {
  0% {transform: rotate(0deg);}
  100% {transform: rotate(90deg);}
}
div {
  animation: rotate 1s ease-in-out;
}

div {
  transition: transform 1s ease;
}
div:hover {
  transform: translateX(100px) scale(1.2);
}

p {
  transition: opacity 2s ease-in-out;
}
p:hover {
  opacity: 0.2;
}

@keyframes fadeOut {
  0% {opacity: 1;}
  100% {opacity: 0;}
}
div {
  animation: fadeOut 3s linear;
}

div {
  transition: transform 0.6s;
}
div:hover {
  transform: rotate(180deg) scale(1.5);
}

div {
  transition: transform 1s ease-in;
}
div:hover {
  transform: translateY(-100px);
}

div {
  background-color: yellow;
  transition: background-color 2s ease;
}
div:hover {
  background-color: red;
}

@keyframes shake {
  0% {transform: translateX(0);}
  50% {transform: translateX(-10px);}
  100% {transform: translateX(0);}
}
div {
  animation: shake 0.5s linear;
}

button {
  transition: background-color 1s linear;
}
button:hover {
  background-color: green;
}

div {
  transition: all 0.3s ease;
  width: 100px;
  height: 100px;
  background-color: blue;
}
div:hover {
  width: 150px;
  height: 150px;
  background-color: red;
}

div {
  transition: transform 1s ease-out;
}
div:hover {
  transform: scale(1.5);
}

div {
  transition: background-color 0.5s ease;
}
div:hover {
  background-color: lightblue;
}

@keyframes zoomIn {
  0% {transform: scale(1);}
  100% {transform: scale(2);}
}
div {
  animation: zoomIn 2s ease;
}

div {
  transition: height 1.5s ease-in-out;
  height: 100px;
}
div:hover {
  height: 200px;
}

div {
  transition: opacity 2s ease;
}
div:hover {
  opacity: 0.5;
}

@keyframes expand {
  0% {width: 100px;}
  100% {width: 300px;}
}
div {
  animation: expand 4s ease-in;
}

div {
  transition: width 2s ease-in-out;
  width: 100px;
}
div:hover {
  width: 300px;
}

@keyframes slideRight {
  0% {left: 0;}
  100% {left: 200px;}
}
div {
  position: relative;
  animation: slideRight 3s ease;
}

div {
  transition: height 1s linear;
  height: 150px;
}
div:hover {
  height: 250px;
}

@keyframes colorChange {
  0% {background-color: blue;}
  100% {background-color: green;}
}
div {
  animation: colorChange 2s ease-in-out;
}

button {
  transition: all 0.4s ease;
}
button:hover {
  background-color: yellow;
  border-radius: 50%;
}

@keyframes growHeight {
  0% {height: 50px;}
  100% {height: 200px;}
}
div {
  animation: growHeight 5s ease-in;
}

@keyframes fadeIn {
  0% {opacity: 0;}
  100% {opacity: 1;}
}
img {
  animation: fadeIn 3s ease-out;
}

div {
  transition: transform 1s ease-in;
  transform: rotate(0deg);
}
div:hover {
  transform: rotate(45deg);
}

@keyframes textGrow {
  0% {font-size: 16px;}
  100% {font-size: 32px;}
}
p {
  animation: textGrow 2s linear;
}

@keyframes moveUp {
  0% {top: 100px;}
  100% {top: 0;}
}
div {
  position: relative;
  animation: moveUp 3s ease-in;
}

@keyframes pulse {
  0% {transform: scale(1);}
  50% {transform: scale(1.2);}
  100% {transform: scale(1);}
}
div {
  animation: pulse 2s infinite ease-in-out;
}

div {
  transition: background-color 0.3s ease;
  background-color: blue;
}
div:hover {
  background-color: pink;
}

div {
  transition: transform 0.8s linear;
}
div:hover {
  transform: scale(1.3);
}

@keyframes shrink {
  0% {width: 300px;}
  100% {width: 100px;}
}
div {
  animation: shrink 4s ease;
}

div {
  transition: opacity 1.5s ease-out;
}
div:hover {
  opacity: 0.7;
}

button {
  transition: all 0.3s ease-in;
}
button:active {
  transform: translateY(5px);
}

div {
  transition: border-radius 1s ease;
  border-radius: 0;
}
div:hover {
  border-radius: 50%;
}

@keyframes slideUp {
  0% {bottom: 0;}
  100% {bottom: 50px;}
}
div {
  position: relative;
  animation: slideUp 1s ease-in-out;
}

div {
  transition: background-color 0.5s linear;
  background-color: red;
}
div:hover {
  background-color: purple;
}

@keyframes fadeOutText {
  0% {opacity: 1;}
  100% {opacity: 0;}
}
p {
  animation: fadeOutText 2s linear;
}
