  /* Box Model Basics */
  width: 300px;
  padding: 20px;
  border: 1px solid #ddd;
  margin: 20px auto; /* Centered */

  /* Modern Box Sizing */
  box-sizing: border-box;

  /* Visuals */
  background-color: white;
  box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
  border-radius: 8px;
  text-align: center;
  font-family: Arial, sans-serif;
}

.card:hover {
  box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
}

