h1 {
    color: red; /* 'red' is a value */
    font-size: 24px; /* '24px' is a value */
    text-align: center; /* 'center' is a value */
}

/* Universal selector applies styles to all elements */
* {
    margin: 0;
    padding: 0;
}

/* Group selector applies styles to multiple elements */
h1, h2, p {
    font-family: Arial, sans-serif;
}

a:hover {
    color: red;
}
input:focus {
    background-color: yellow;
}

p::first-letter {
    font-size: 24px;
    color: red;
}
