answer.content:
body {
  background-color: #3498db; /* Hex Format */
  background-color: rgb(52, 152, 219); /* RGB Format */
}

div {
  background-color: rgba(255, 0, 0, 0.5); /* Red with 50% opacity */
}

body {
  background: linear-gradient(to right, blue, green);
}

div {
  background: radial-gradient(circle, yellow, black);
}

body {
  background-image: url('background.jpg');
  background-size: cover;
  background-repeat: no-repeat;
}

body {
  background-color: hsl(200, 70%, 50%); /* Blue shade */
}

div {
  background-color: hsla(120, 100%, 50%, 0.5); /* Semi-transparent green */
}

body {
  background: linear-gradient(45deg, red, blue);
}

div {
  background: repeating-linear-gradient(90deg, yellow, yellow 10px, black 10px, black 20px);
}

div {
  background-image: url('pattern.png'), url('background.jpg');
  background-size: auto, cover;
  background-repeat: repeat, no-repeat;
}

body {
  background-image: url('background.jpg');
  background-attachment: fixed;
}

div {
  background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url('background.jpg');
  background-size: cover;
}

.background {
  background-image: url('background.jpg');
  filter: blur(5px);
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: -1;
}

div {
  background-color: rgba(255, 0, 0, 0.5); /* Semi-transparent red */
}

h1 {
  background: linear-gradient(to right, blue, purple);
  -webkit-background-clip: text;
  color: transparent;
}

div {
  background: radial-gradient(circle, yellow, orange, red);
}

div {
  border: 5px solid;
  border-image: linear-gradient(to right, blue, purple) 1;
}

button {
  background: linear-gradient(to right, blue, green);
  border: none;
  padding: 10px 20px;
  color: white;
  cursor: pointer;
}
button:hover {
  background: linear-gradient(to right, red, yellow);
}

div {
  position: relative;
}
div::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url('background.jpg');
  opacity: 0.5;
  z-index: -1;
}

div {
  background: radial-gradient(circle, rgba(255, 0, 0, 0.5), transparent),
              linear-gradient(to right, blue, purple);
}

body {
  background: radial-gradient(circle, black 10%, transparent 10%) 0 0 / 20px 20px;
}

body {
  background: url('background.jpg') fixed;
  background-size: cover;
}
