answer.content:
div p:first-child {
  font-weight: bold;
  color: red;
}

h1::after {
  content: '';
  display: block;
  width: 100%;
  height: 3px;
  background-color: black;
}

p::first-letter {
  font-size: 2em;
  color: blue;
  font-weight: bold;
}

h2::before {
  content: '';
  display: inline-block;
  width: 5px;
  height: 100%;
  background-color: red;
  margin-right: 10px;
}

button:hover::after {
  content: 'Click me!';
  display: block;
  background: black;
  color: white;
  padding: 5px;
  position: absolute;
  top: -30px;
  left: 50%;
  transform: translateX(-50%);
  border-radius: 5px;
}

a {
  text-decoration: none;
}
a:hover {
  text-decoration: underline;
}

input[type='checkbox']:checked + label {
  color: green;
  font-weight: bold;
}

h1::after {
  content: '';
  display: block;
  width: 100%;
  height: 2px;
  background-color: black;
  margin-top: 10px;
}
