// 1. Child Component
function UserAvatar({ imgUrl, name }) {
  return (
    <div className="avatar">
      <img src={imgUrl} alt={name} width="50" />
      <span>{name}</span>
    </div>
  );
}

// 2. Parent Component
function UserCard() {
  // Data to pass down
  const user = {
    name: "Alice",
    pic: "https://via.placeholder.com/50"
  };

  return (
    <div className="card">
      <h2>User Profile</h2>
      {/* Passing props */}
      <UserAvatar imgUrl={user.pic} name={user.name} />
    </div>
  );
}
