import { useState } from 'react';

function Toggle() {
  const [isOn, setIsOn] = useState(false);

  const toggle = () => {
    setIsOn(prevIsOn => !prevIsOn);
  };

  return (
    <button 
      onClick={toggle}
      style={{ backgroundColor: isOn ? 'green' : 'red' }}
    >
      {isOn ? 'ON' : 'OFF'}
    </button>
  );
}
