<!-- Modal.svelte -->
<script>
	import { createEventDispatcher } from 'svelte';

	const dispatch = createEventDispatcher();

	function close() {
		dispatch('close');
	}
</script>

<div class="modal-layout">
	<div class="modal">
		<h1>Hello!</h1>
		<button on:click={close}>Close Modal</button>
	</div>
</div>

<style>
