function createStore() {
  const { subscribe, update } = writable(initialState);
  return {
    subscribe,
    addItem: (item) => update(s => ({ ...s, items: [...s.items, item] })),
    removeItem: (id) => update(s => ({ ...s, items: s.items.filter(i => i.id !== id) }))
  };
}

