function createValidatedStore(initial, validate) {
  const { subscribe, set, update } = writable(initial);
  return {
    subscribe,
    set: (v) => validate(v) && set(v),
    update: (fn) => update(s => {
      const n = fn(s);
      return validate(n) ? n : s;
    })
  };
}

