import { version } from '$service-worker';

self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then(keys =>
      Promise.all(
        keys.filter(key => key !== `cache-${version}`)
          .map(key => caches.delete(key))
      )
    )
  );
});

