export function load({ cookies }) {
  const session = cookies.get('session');

  if (!session) {
    cookies.set('session', crypto.randomUUID(), {
      path: '/',
      httpOnly: true,
      maxAge: 60 * 60 * 24
    });
  }

  return { hasSession: !!session };
}

