// clickOutside.js
export function clickOutside(node, callback) {
  const handler = (e) => {
    if (!node.contains(e.target)) callback();
  };
  document.addEventListener('click', handler);
  return {
    destroy() {
      document.removeEventListener('click', handler);
    }
  };
}

