function undoable(initial) {
  const history = [initial];
  let index = 0;
  const { subscribe, set } = writable(initial);
  return {
    subscribe,
    set: (v) => { history.push(v); index = history.length - 1; set(v); },
    undo: () => { if (index > 0) set(history[--index]); }
  };
}

