const mouse = readable({ x: 0, y: 0 }, set => {
  const handler = e => set({ x: e.clientX, y: e.clientY });
  window.addEventListener('mousemove', handler);
  return () => window.removeEventListener('mousemove', handler);
});

const windowSize = readable({ width: innerWidth, height: innerHeight }, set => {
  const handler = () => set({ width: innerWidth, height: innerHeight });
  window.addEventListener('resize', handler);
  return () => window.removeEventListener('resize', handler);
});

function mediaQuery(query) {
  return readable(false, set => {
    const mql = window.matchMedia(query);
    const handler = e => set(e.matches);
    set(mql.matches);
    mql.addEventListener('change', handler);
    return () => mql.removeEventListener('change', handler);
  });
}

const online = readable(navigator.onLine, set => {
  const on = () => set(true);
  const off = () => set(false);
  window.addEventListener('online', on);
  window.addEventListener('offline', off);
  return () => {
    window.removeEventListener('online', on);
    window.removeEventListener('offline', off);
  };
});

const location = readable(null, set => {
  const watchId = navigator.geolocation.watchPosition(
    pos => set({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
    err => set({ error: err.message })
  );
  return () => navigator.geolocation.clearWatch(watchId);
});

